import React from 'react'
import { Line } from 'react-chartjs-2'
import { CDBContainer } from 'mbdreact'

const DataChart = ({ filteredData, unit }) => {

  const datalist = filteredData.map(d => d.value)
  const dates = filteredData.map(d => d.date)
  const data = {
    labels: dates,
    datasets: [
      {
        label: unit,
        data: datalist,
        fill: true,
        backgroundColor: 'green',
        borderColor: 'blue'
      }
    ]
  }

  return (
    <CDBContainer>
      <Line data={data} />
    </CDBContainer>
  )
}

export default DataChart
